import React, { useState } from "react";

// could send down props instead
function Prices({ bpi }) {
  const [currency, setCurrency] = useState("USD");

    const handleSubmit =(e)=>{
        e.preventDefault()
    }
  return (
    <div>
      <ul className="list-group">
        <li className="list-group-item">
          The current rate is {bpi.USD.description}:  
          <span className="badge badge-primary"> {bpi.USD.code}</span>
          <span> {bpi.USD.rate}</span>
        </li>
      </ul>
      <div>
          Pick your currency
          <form onSubmit={handleSubmit}>
              <input type="string" value={currency} onChange={e => setCurrency(e.taget.value)}/>
          </form>
      </div>

    </div>
  );
}

export default Prices;
