import React, { useEffect, useSate } from "react";

const FetchData = url => {
  const [state, setState] = useSate({ data: null, loading: true });

  useEffect(() => {
    fetch(url)
      .then(x => x.text())
      .then(y => 
        setState);
  }, [url]);

  return state
};

export default FetchData;
